import React from "react";
import { ProjectStyle } from "./ProjectStyle";

import Link from "next/link";

import FontH1 from "../../styles/fontsStyles/fonth1"
import FontP from "../../styles/fontsStyles/fontp"

import Button from "../Button/Button";


const Project = () => {
  const [view, setView] = (false);
  const handleOpen = () => {
    const value = !view;
    setView(value);
  };
  return (
    <ProjectStyle view={view}>
      <div>
        <FontP onClick={handleOpen} cursor={"true"}>Hola</FontP>

      </div>
      <div className="Desktop">
        <div>
          <img cursor={"true"} onClick={handleOpen} src="/static/svg/closeMenu.svg" />
        </div>
        <div></div>
        <FontH1>Soy Simón G. Negrete. Trabajo como Director de Arte y Desarrollo. </FontH1>
        <FontP>Especializado en branding y producto, acompaño al cliente desde la conceptualización de la idea, diseño y implementación tanto en entorno online como offline. </FontP>
        <div className="button">
          <Link href=""><Button content="Diseño" color="#F1A409" backgroundColor="#FCEDCE" /></Link>
          <Link href=""><Button content="Web" color="#F1A409" backgroundColor="#FCEDCE" /></Link>
        </div>
      </div>

    </ProjectStyle>
  );
};

export default Project;
