import styled from 'styled-components';

const ProjectStyle = styled.section`
  width:90%;
  margin: 0 auto;
  .img{
    background-image:${props => props.img ? `url(${props.img})` : "img"} no-repeat fixed center;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-color: ${props => (props.img ? '' : 'black')};
    background-size: cover;
    height: 100%;
    width: 100% ;
    text-align: center;
  }
  .info{
    width:95%;
    margin:0 auto;
    display:flex;
    justify-content: space-between;
    align-items: center;
  }

