import styled from 'styled-components';

const ProjectStyle = styled.section`
  width:90%;
  margin: 0 auto;
  .img{
    background-image:${props => props.photo ? `url(${props.photo})` : "photo"};
    background-color: ${props => (props.photo ? '' : '#fffff')};
    width:100%;
    height:50%;
    max-height:500px;
  }
